package set;

import java.util.ArrayList;
import java.util.List;

public class Set_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		{
			List <String> name= new <String> ArrayList();
			name.add("HI");
			name.add("HELLO");
			System.out.println(name);
			name.set(0, "II");
			System.out.println(name);
		}
		
		
		
		
	}

}
